import React, { Component } from "react";
import {Link} from 'react-router';
import { connect } from "react-redux";
import {getTotalPrice,getProductCount} from "selectors";
export class BasketCart extends Component {
  constructor(){
    super();
    this.state = {
      price:0,
      count:0
    };
  }
  componentDidMount(){
    this.setState({price:this.props.totalPrice,count:this.props.totalProducts});
  }
  componentWillReceiveProps(nextProps){
    this.setState({price:nextProps.totalPrice,count:nextProps.totalProducts});

  }
  render() {
      
      const {price,count} = this.state;
    return (
      <div>
        <div className="cart">
          <div className="dropdown">
            <Link
              to="/basket"
              id="dLabel"
              className="btn btn-inverse btn-block btn-lg"
            >
              <i className="fa fa-fa-shopping-cart">
                <span>{`items ${count?count:0} Total ${price}`}</span>
              </i>
            </Link>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  
  totalProducts:getProductCount(state),
  totalPrice:getTotalPrice(state)
});

const mapDispatchToProps = {};

export default connect(
  mapStateToProps,
  null
)(BasketCart);
