import { ADD_TO_CART_SUCCESS, REMOVE_FROM_CART_SUCCESS } from "actionTypes";
const initialState = [];

export default (state = initialState, action) => {
  switch (action.type) {
    case ADD_TO_CART_SUCCESS:
      return Object.assign([], state, state.push(action.payload));
    case REMOVE_FROM_CART_SUCCESS: {
        let cartToBeMutated = state;
        cartToBeMutated.splice(state.indexOf(action.payload),1);
        return Object.assign([],state,cartToBeMutated)
    }
    default:
      return state;
  }
};
