import { FETCH_PHONES_SUCCESS,LOAD_PHONES_SUCCESS, FETCH_EACH_PHONES_SUCCESS } from "actionTypes";
const initialState = {};

export default (state = initialState, action) => {
  switch (action.type) {
    case FETCH_PHONES_SUCCESS:
      return Object.assign([], state, action.payload);
      case LOAD_PHONES_SUCCESS:
      return Object.assign([], state, state.concat(action.payload));
    default :
      return state;
      
  }
};
