export const getPhones = state => {
  return state.phones;
};

export const getProductCount = state => {
  let count = 0;
  state.cart.map((product)=>{
    count+=parseInt(product.count);
  });
  return count;
};

export const getCartProducts = state => {
  return state.cart;
};

export const getTotalPrice = state => {
  const { cart } = state;
  let totalPrice = 0;
  cart &&
    cart.map(cartProduct => {
      totalPrice += parseInt(cartProduct.price*cartProduct.count);
    });
  return totalPrice;
};

