export const getPhones = state => {
  return state.phones;
};

export const getProductCount = state => {
    return state.cart.length;
  };
  
export const getCartProducts = state => {
  let cartWithoutFilter = state.cart;
  let simplifiedCart = [];

  cartWithoutFilter.map(product => {
    let count = 0;
    for (let i = 0; i < cartWithoutFilter.length; i++) {
      if (product.id === cartWithoutFilter[i].id) {
        count++;
      }
    }
    Object.assign(product, product, { count: count });
  });
  simplifiedCart = cartWithoutFilter.filter(
    (product, pos) => cartWithoutFilter.indexOf(product) === pos
  );

  return simplifiedCart;
};

export const getTotalPrice = state => {
  const { cart } = state;
  let totalPrice = 0;
  cart &&
    cart.map(cartProduct => {
      totalPrice += cartProduct.price;
    });
  return totalPrice;
};
